def find_prime_numbers(n):
    primes = []

    for i in range(2, n + 1):
        is_prime = True
        for j in range(2, i):
            if i % j == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(i)

    return primes

def textToInt(text):
    int_val = 0
    for char in text:
        int_val += ord(char)
    return int_val

def next_prime(n):
    '''
    Returns the next prime number after the number n
    Inputs:
        n (int): The number that the API should start from
    Outputs:
        The next prime number
    '''
    num = n + 1
    while True:
        for i in range(2, num):
            if num % i == 0:
                num += 1
                break
        else:
            return num
